import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";

class Detail extends Component {
  state = {
    movie: {}
  };
  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc
  componentDidMount() {
    API.getMovie(this.props.match.params.id)
      .then(res => this.setState({ movie: res.data }))
      .catch(err => console.log(err));
  }

  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1>
                {this.state.movie.title} on {this.state.movie.director}
              </h1>
            </Jumbotron>
          </Col>
        </Row>
        <Row>
          <Col size="md-10 md-offset-1">
            <article>
              <Col size="md-4">
                <img className="thumbnail" role="img" src={this.state.movie.image}></img>
              </Col>
              <Col size="md-6">
                <h1>Cast</h1>
                <p>
                  {this.state.movie.cast}
                </p>
              </Col>
              <h1>Plot</h1>
              <p>
                {this.state.movie.plot}
              </p>
              <p>
              <a href={this.state.movie.link}>Link to more details</a>
              </p>
            </article>
          </Col>
        </Row>
        <Row>
          <Col size="md-2">
            <Link to="/">‚Üê Back to Directors</Link>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Detail;
